import {withNavigation} from "react-navigation";
import * as Progress from 'react-native-progress';


const Result = () => {

  const [color, setColor] = useState("rgba(212, 2, 119, 1)")
  //rgb(212,2,119)


  useEffect(() => {
    process();
  }, []);

  function process() {
    const array = global.myVar
    array.map(element => stockAPI(element))
  }

  const [data, setData] = useState(null);

    function stockAPI(element) {

        console.log(elememt)
      fetch('https://api.polygon.io/v2/aggs/ticker/' + element + '/range/1/day/2021-07-22/2022-07-22?adjusted=true&sort=asc&limit=120&apiKey=HSMNRUITPGqoXqgpUlgWjaTNpP28ItWQ')
        .then((response) => response.json())
        .then((data) => setData(data));      

        console.log(data);

    }

  return (
    <>
    <Text style={styles.header}>results from the array</Text>
    </>
  );



}

const styles = StyleSheet.create({

    container: {
      alignItems: 'center',
      justifyContent: 'center',
      position: 'absolute',
      top: 20,
    },
    header: {
      fontSize: 13,
      color: 'white',
      marginBottom: 20,
      // fontWeight: 800,
    },
    button: {
      alignItems: 'center',
      width: 200,
      justifyContent: 'center',
